import React, { useEffect } from 'react'

export const Message = () => {

    useEffect(() => {
        console.log('montando el componente'); //cuerpo del useEffect
        return () => {
            console.log('desmontando el componente'); //limpiar el useEffect
        }
    }, [])

    return (
        <div>
            <h1>Mensaje desde otro Componente</h1>
        </div>
    )
}
