import { useState } from "react"

export const useForm = (initialState = {}) => {
    const [value, setValue] = useState(initialState);

    const handleInputChange = ({target}) =>{
        setValue({
            ...form,
            [target.name]: target.value
        });
    }

    return [value, handleInputChange];

}
