const initialState = [{
    id: 1,
    todo: 'comprar Pan',
    done: false
}];


const todoReducer = (state = initialState, action) => {



    return  state;
}

let todos = todoReducer();

//nuevo arreglo con un nuevo objeto
const newTodo = [{
    id: 2,
    todo: 'comprar Harina',
    done: false
}];

const agregarTodoAction = {
    type: 'agregar',
    payload: newTodo
}

todos = todoReducer(todos,agregarTodoAction);

console.log(todos);
