import React, { useEffect, useReducer } from 'react'
import { todoReducer } from './todoReducer'
import { useForm } from '../../hooks/useForm'
import { TodoList } from './TodoList'

import './style.css'

const init = () =>{
    return JSON.parse(localStorage.getItem('todos')) || [];
}

export const TodoApp = () => {

    const [todos, dispatch] = useReducer(todoReducer, [], init)
    const [{description}, handleInputChange, reset] = useForm({
        description: ''
    }); 

    useEffect(() => {
        localStorage.setItem('todos', JSON.stringify(todos));
    }, [todos])

    const handleToggle = (todosId) => {
        dispatch({
            type: 'toggle',
            payload: todosId
        })
    }

    const handleDelete = (todosId) => {

         const action = {
            type:'delete',
            payload: todosId
        }

        dispatch(action);
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        if(description.trim().length <= 1 ){
            return;
        }

        const newTodo ={
            id: new Date().getTime(),
            desc: description,
            done: false
        }

        const action = {
            type: 'add',
            payload: newTodo
        }

        dispatch(action);
        reset();
    }

    return (
        <div>
            <h1>TodoApp ( {todos.length} )</h1>
            <hr></hr>

            <div className="row">

                <TodoList 
                todos = {todos} 
                handleDelete = {handleDelete}
                handleToggle = {handleToggle}
                />

                {/* <div className="col-7">
                    <ul className="list-group list-group-flush">
                        {
                            todos.map((todo, i) => (
                                <li key={todo.id}
                                className="list-group-item"
                                >
                                    <p className={`${todo.done && 'complete'}`} onClick={() => handleToggle(todo.id) }> {i + 1}) {todo.desc}</p>
                                    <button className="btn btn-danger" onClick={() => handleDelete(todo.id)}>
                                        Borrar
                                    </button>
                                </li>
                            ))
                        }
                    </ul>
                </div> */}
                <div className="col-5">
                    <h4>Agregar TODO:</h4>
                    <hr></hr>

                    <form onSubmit={handleSubmit}>
                        <input 
                            className="form-control" 
                            type="text"
                            name="description"
                            placeholder="Aprender..."
                            autoComplete="off"
                            value={description}
                            onChange={handleInputChange}
                        ></input>    
                        <button className="btn btn-outline-primary mt-2 btn-block" type="submit">Agregar</button>

                    </form>
                </div>

            </div>
        </div>
    )
}
